import React, { useRef } from "react";
import {useNavigate} from 'react-router-dom';

import './UploadBtn.css'

const UploadButton = props => {
    const history = useNavigate();
    history('/GenerateSchedule')

    const fileRef = useRef();

    const handleChange = (e) => {
      const [file] = e.target.files[0];
      console.log(file.name);
    };

    return (
        <React.Fragment>
            <div ></div>
            <button className="upload-button" onClick={() => fileRef.current.click()} >Upload Datasets</button>
            <input type="file" ref={fileRef} onChange={handleChange} style={{display:'none'}}  accept=".csv" />
        </React.Fragment>
    );
};

export default UploadButton;
