import React,{ useState, useCallback } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Routes
} from 'react-router-dom';

import WelcomePage from './components/WelcomePage/WelcomePage'
import GenerateSch from './components/GenerateSchedulePage/GenerateSchedulePage';
import UploadPage from './components/UploadPage/UploadPage';
import ArraySchedule from './components/RandomSchedule/arraySchedule';
import Dataset from './components/GetDataset/getDataset';
import { AuthContext } from './components/contexts/auth-context';

import './App.css';

const App = () =>{

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(false);

  const login = useCallback(uid => {
    setIsLoggedIn(true);
    setUserId(uid);
  }, []);

  const logout = useCallback(() => {
    setIsLoggedIn(false);
    setUserId(null);
  }, []);

  let routes;

  if (isLoggedIn) {
    routes = (
      <Routes>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/UploadPage" exact>
          <UploadPage />
        </Route>
        <Route path="/:userId/GenerateSchedule" exact>
          <GenerateSch />
        </Route>
        <Route path="/:userId/file">
          <Dataset />
        </Route>
        <Route path="/:userId/ArraySchedule">
          <ArraySchedule />
        </Route>
        <Redirect to="/" />
        {/* <Route path="/UploadPage" exact element ={<UploadPage />} />
        <Route path="/GenerateSchedule" exact element={<GenerateSch />} />
        <Route path="/Dataset" exact element={<Dataset />} />
        <Route path="/ArraySchedule" exact element={<ArraySchedule />} />     */}
      </Routes>
    );}
  return (
    <AuthContext.Provider
    value={{
      isLoggedIn: isLoggedIn,
      userId: userId,
      login: login,
      logout: logout
    }}
  >
      <Router>
        <WelcomePage />
        <main>{routes}</main>
      </Router>
      </AuthContext.Provider>
  );
}

export default App;
