/** Convert a 2D array into a CSV string
 */
  function arrayToCsv(data){
    return data.map(row =>
      row
      .map(String)  // convert every value to String
      .map(v => v.replaceAll('"', '""'))  // escape double colons
      .map(v => `"${v}"|`)  // quote it
      .join('|')  // comma-separated
    ).join('\r\n');  // rows starting on new lines
  }

