import React,{ useEffect,useState, useRef } from "react";
import { CSVLink } from "react-csv";
import LoadingSpinner from "../Modal/LoadingSpinner";

import './GenerateSchedulePage.css';

const GenerateSch = props => {
    const [isLoading, setIsLoading] = useState(false);
    const [loadedFile, setLoaadedFile] = useState(false);
    const [CsvData, setCsvData] = useState();
    const [Keys, setKeys] = useState();
    //console.log("props dataset:"+ props.test)
    useEffect(()=>{
        const sendRequest = async () =>{
            setIsLoading(true);
            try{
            // const response = await fetch('http://localhost:5000/api/user/');
            const response = await fetch('http://localhost:5000/api/files/users/624278b29a90a9077e2f65e4');
            const responsedata = await response.json();
            let stringifyData = JSON.stringify(responsedata.userWithFiles.Dataset);
            let ertData =JSON.stringify(responsedata.userWithFiles.Dataset[0].Dataset);
            
            let ertLocation = responsedata.userWithFiles.Dataset[0].Dataset.map(Dataset => Dataset['Location']); //Extract Location
            const ertRoom = responsedata.userWithFiles.Dataset[0].Dataset.map(Dataset => Dataset['Room']); //Extract Room
            const  ertSize= responsedata.userWithFiles.Dataset[0].Dataset.map(Dataset => Dataset['Size']); //Extract Size
            const ertSlot = responsedata.userWithFiles.Dataset[0].Dataset.map(Dataset => Dataset['Slot']); //Extract Slot

            const ertCCode = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Course Code']); //Extract Course Code
            const ertCName = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Course Name']); //Extract Course Code
            const ertCHours = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Credit Hours']); //Extract Course Code
            const ertYear = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Year']); //Extract Course Code
            const ertSemester = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Semester']); //Extract Course Code
            const ertProCode = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Program Code']); //Extract Course Code
            const ertTtlStudent = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Total Student']); //Extract Course Code
            const ertLecturer = responsedata.userWithFiles.Dataset[1].Dataset.map(Dataset => Dataset['Lecturer']); //Extract Course Code
            // console.log("X value= " + ertCName);
            // for (let i = 0 ; i<test1.length; i++){
            //      let x = test1[i].Location;
            //      console.log("X value= " +x);
            // }
            //console.log("Test 1: "+ test1);
            //let stringifyData = JSON.stringify(responsedata.files.Dataset);
            //let setK = Object.keys(responsedata.files.Dataset[1])
            //setKeys([setK])
            //console.log("STRINGIFYDATA: " + stringifyData);
            //const test1 = responsedata.files.Dataset.map(Dataset => Dataset['Location']);
            //console.log("Test: " + test1);
            setCsvData([ertLocation]);
            //console.log("Get Data: "+ JSON.stringify(responsedata));
            if (!response.ok){
                throw new Error(responsedata.message);
            }
            setLoaadedFile(responsedata.users);
            setIsLoading(true);
            }catch(err){
                console.log(err.message);
            }
            setIsLoading(false);
            
        };
        sendRequest();
    },[]);
    let headers = [
        { label: "Location", key:Keys  },
        // { label: "Room", key: "Room" },
        // { label: "Size", key: "Size" },
        // { label: "Slot", key: "Slot" }
      ];

      //console.log("CsvData: " + CsvData);


    return (
        <div className="GenerateSch-section">
                            {/* {CsvData && CsvData.map((Dataset)=>(
                    <h2>{Dataset}</h2>
                ))} */}
            <h1>
                Generate Schedule Page
                { CsvData && CsvData.map((Dataset) =>(<CSVLink data = {Dataset} headers={headers}>Download me</CSVLink>))}
                {/* {isLoading && <div className="center"><LoadingSpinner/></div>} */}
            </h1>
            {/* {!isLoading && loadedFile} */}
        </div>
    );
};

export default GenerateSch;
