import React,{ useEffect,useState, useContext } from "react";
import { CSVLink } from "react-csv";
import LoadingSpinner from "../Modal/LoadingSpinner";

import './GenerateSchedulePage.css';

const GenerateSch = props => {
    const [isLoading, setIsLoading] = useState(false);
    const [loadedFile, setLoaadedFile] = useState(false);
    const [CsvData, setCsvData] = useState();
    //console.log("props dataset:"+ props.test)
    useEffect(()=>{
        const sendRequest = async () =>{
            setIsLoading(true);
            try{
            const response = await fetch('http://localhost:5000/api/files/62453d78d4c3bc106b042909');
            const responsedata = await response.json();
            //let stringifyData = JSON.stringify(responsedata.Dataset);
            let stringifyData = JSON.stringify(responsedata.files.Dataset);
            setCsvData([stringifyData]);
            //console.log("Get Data: "+ stringifyData);
            if (!response.ok){
                throw new Error(responsedata.message);
            }
            setLoaadedFile(responsedata);
            setIsLoading(true);
            }catch(err){
                console.log(err.message);
            }
            setIsLoading(false);
        };
        sendRequest();
    },[]);
    console.log("CSV: " + CsvData);
    let headers = [
        { label: "Location", key:CsvData.Location  },
        // { label: "Room", key: "Room" },
        // { label: "Size", key: "Size" },
        // { label: "Slot", key: "Slot" }
      ];

      //console.log("CsvData: " + CsvData);


    return (
        <div className="GenerateSch-section">
                            {/* {CsvData && CsvData.map((Dataset)=>(
                    <h2>{Dataset}</h2>
                ))} */}
            <h1>
                Generate Schedule Page
                {CsvData && CsvData.map((Dataset) =>(<CSVLink data = {Dataset} headers={headers}>Download Me</CSVLink>))}
                {isLoading && <div className="center"><LoadingSpinner/></div>}
            </h1>
        </div>
    );
};

export default GenerateSch;
