                        filterDataElective200.push(courseDataWithElective[i]);
                    }
                    if (courseDataWithElective[i][0] >= 200 && courseDataWithElective[i][0] < 500) {
                        filterDataElective500.push(courseDataWithElective[i]);
                    }
