import React,{ useEffect,useState } from "react";
import { CSVLink } from "react-csv";

import LoadingSpinner from "../Modal/LoadingSpinner";

const Dataset = () => {

    const [isLoading, setIsLoading] = useState(false);
    const [loadedFile, setLoaadedFile] = useState(false);
    useEffect(()=>{
        const sendRequest = async () =>{
            setIsLoading(true);
            try{
            const response = await fetch('http://localhost:5000/api/uploadFile/621dd38cdfb2955ee60189ce');
            const responsedata = await response.json();
            console.log(responsedata.Dataset.Headers);
            if (!response.ok){
                throw new Error(responsedata.message);
            }
            setLoaadedFile(responsedata);
            setIsLoading(true);
            }catch(err){
                console.log(err.message);
            }
            setIsLoading(false);
        };
        sendRequest();
    }, []);

    return (
        <React.Fragment>
            {isLoading && <div className="center"><LoadingSpinner/></div>}
        </React.Fragment>
    );
};

export default Dataset;
