import React from "react";

const ArraySchedule = () => {
    //const Schedule = new Array(250).fill(Course);
    
    //console.log(Schedule);
    // const Course = [['TE1',4],['TE1',4],['TE1',4],['TE1',3],['TE1',4],['TE1',2],['TE1',4],['TE1',4],['TE1',4],['TE1',3]];
    const Course = ['TE1','TE2','TE3','TE4','TE5','TE6','TE7','TE8','TE9'];
    //let Schedule = new Array(250);

    //Create 50 slot of Class from monday to friday (ROMS)
    //Create 10 slot of Room from monday to friday
    const Schedule = new Array(50)
    const size = Schedule.length

    const Create2dArray = () => {
        var Sche2d = [];
        for (let x = 0; x < size; x ++){
            Schedule[x] = new Array(10).fill(null);
            Sche2d = Schedule[x];
        }

        return Sche2d;
    }


    //console.log(size);
    const random = (rand) => {
        const nullLocation = rand.map((item,index)=> (item === null ? index:null)).filter((item)=> item !== null);
        for(let i = 0; i< Course.length; i++){
            rand[nullLocation[Math.floor(Math.random() * nullLocation.length)]] = Course[i];    
        }
        //rand[nullLocation[Math.floor(Math.random() * nullLocation.length)]] = Course[0];
        return rand
    };

    console.log(random(Schedule));
    //console.log(Schedule);

    return(
        <React.Fragment>
        </React.Fragment>
    );
}

export default ArraySchedule;
