            setcourseData([ertCourse]);

            if (!response.ok){
                throw new Error(responsedata.message);
            }
            setLoaadedFile(stringifyData);
            setIsLoading(true);
            }catch(err){
                console.log(err.message);
            }
            setIsLoading(false);
            
        };
        sendRequest();
    },[userId]);

        if(!isLoading && loadedFile){
            const testData = [1,2,3,4,5,6,7,8,9];
