import React from "react";
import {useNavigate} from 'react-router-dom';

import './UploadBtn.css'

const UploadButton = props => {
    const history = useNavigate();
    history('/GenerateSchedule')

    const hiddenFileInput = React.useRef(null);
  
    const handleClick = event => {
      hiddenFileInput.current.click();
    };
    const handleChange = event => {
      const fileUploaded = event.target.files[0];
      props.handleFiles(fileUploaded);
    };

    return (
        <React.Fragment>
          <input id="contained-button-file" type="file" style={{display:'none'}}  accept=".csv" />
          <button htmlFor="contained-button-file" className="upload-button" component="span">Upload Datasets</button>
            
        </React.Fragment>
    );
};

export default UploadButton;
