import React, { useRef} from "react";
import {useNavigate} from 'react-router-dom';

import './UploadBtn.css'

const UploadButton = props => {
    const history = useNavigate();
    history('/GenerateSchedule')

    const fileRef = useRef();

    const handleChange = (e) => {
      const [file] = e.target.files;
      const filename = e.target.files.name;
      console.log(file);
    };

    return (
        <React.Fragment>
            <p onChange={handleChange}>{handleChange.filename}</p>
            <input id="file-upload" type="file" name="file" ref={fileRef} onChange={handleChange} style={{display:'none'}}  accept=".csv"/>
            <button className="upload-button" onClick={() => fileRef.current.click()} >Upload Datasets</button>
        </React.Fragment>
    );
};

export default UploadButton;
