import React from "react";


import Course from '../../image/CourseDataset.png';
import Room from '../../image/RoomDataset.png';
import GenerateButton from "./GenerateBtn";
import UploadButton from "./UploadBtn";
import './UploadPage.css'

const UploadPage = props => {
    const fileInput = React.useRef(null);
    const UploadFilehandler = event =>{
        //document.getElementById('fileUpload');
        fileInput.current.click()
    };

    const handleChange = event => {
        const fileUploaded = event.taraget.files[0];
        props.UploadFilehandler(fileUploaded);
    };

    return (
        <React.Fragment>
            <div className="upload-page">
                <div className="upload-page__content">
                    <img src={Course} alt="CourseDataset"></img>
                    <img src={Room} alt="RoomDataset"></img>
                </div>
                <div className="upload-page__textbox"></div>
                <div className="upload-page__button">
                    <form>
                        <input id="fileUpload" type="file" style={{display:"none"}} ref={fileInput} onChange={handleChange}/>
                            <UploadButton onClick={UploadFilehandler} ></UploadButton>
                    </form>
                    <GenerateButton></GenerateButton>  
                </div>
            </div>
        </React.Fragment>
        
    );
};

export default UploadPage;
