import React, { useRef} from "react";
import {useNavigate} from 'react-router-dom';

import './UploadBtn.css'

const UploadButton = props => {
    const history = useNavigate();
    history('/GenerateSchedule')

    const fileRef = useRef();

    const handleChange = (e) => {
      this.setState({
        fileName: e.target.files[0].name
      });
      const [file] = e.target.files;
      //const filename = e.target.files.name;
      console.log(file);
    };

    return (
        <React.Fragment>
          <p>{this.state.fileName}</p>
            <input id="file-upload" type="file" name="file" ref={fileRef} multiple onChange={handleChange} style={{display:'none'}}  accept=".csv"/>
            <button className="upload-button" onClick={() => fileRef.current.click()} component = "span">Upload Datasets</button>
        </React.Fragment>
    );
};

export default UploadButton;
