import React from "react";
//import {useNavigate} from 'react-router-dom';

import './UploadBtn.css'

class UploadButton extends React.Component{
  state = {
    fileName:''
  }
  fileUploadHandler(e){
    if(e.target.files.length !==0){
      this.setState({fileName:e.target.files[0].name})
    }
  }

  uploadFiles(){
    document.getElementById('selectFile').click()
  }
  render()
  {
    return(
      <>
      <button className="upload-button" onClick={this.uploadFiles.bind(this)}>Upload File</button>
      <input id="selectFile" type='file' onChange={this.fileUploadHandler.bind(this)} style={{display:"none"}} accept=".csv" multiple />
      </>
    )
  }
}
export default UploadButton;
